<template>
<div class="flex flex-col md:flex-row mt-4 justify-center items-center" v-if="response">
    <div  class="md:w-1/3 hidden md:block " id="chart">
        <RM-ChartStategicFront></RM-ChartStategicFront>
    </div>
    <div class="md:w-2/3">
        <h2 class="text-xl">จำแนกตามประเภทความเสี่ยง</h2>
        <div class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 p-4 gap-4">
            <Core-Card3 color="bg-up" icon_size="40" icon=" em em-card_index_dividers" border="border-purple-600" head="ประเด็นความเสี่ยงทั้งหมด" :subHead="risks.length"></Core-Card3>
            <Core-Card3 v-for="type,i in types" :key="i" color="bg-xsa" icon_size="40" icon="em em-hourglass" border="border-red-600" :subHead="type.risks" :head="type.name"></Core-Card3>

        </div>
    </div>
</div>
</template>

<script>
import VueApexCharts from 'vue-apexcharts'
import { Core } from '@/vuexes/core'
import { CoreRisk } from '@/vuexes/core_risk'
import _ from 'lodash'
import { Auth } from '@/vuexes/auth'
import { Web } from '@/vuexes/web'

export default {
    components: {
        apexchart: VueApexCharts,
    },
    data: () => {
        return ({
            response: false,
            user: Auth.user,
            types: [],
            risks: CoreRisk.risks,
            series: [44, 55, 13, 43, 22],
            chartOptions: {
                chart: {
                    width: 380,
                    type: 'polarArea',
                },
                labels: ['Team A', 'Team B', 'Team C', 'Team D', 'Team E'],
                responsive: [{
                    breakpoint: 4000,
                    options: {
                        chart: {
                            width: '100%'
                        },
                        legend: {
                            position: 'bottom'
                        }
                    }
                }]
            },
        })
    },
    async created() {
        this.response = false;
        await this.initialize();
        this.response = true;
    },
    methods: {
        async initialize() {
            await this.getTypes();
        },

        async getTypes() {
            let types = await Core.getHttp(`/api/risktype/?year=${Core.currentYearTxt}`)
            let labels = [];
            let series = []
            for (let i = 0; i < types.length; i++) {
                let val = await this.getData(types[i].id)
                types[i].risks = val
                series.push(val)
                labels.push(types[i].name)
            }
            this.types = types
            this.series = series
            this.chartOptions.labels = labels
        },

        async getData(risk_type) {
            let risks = _.filter(CoreRisk.risks, (o) => {
                return _.find(o.risk_type, (v) => {
                    console.log(v.id, risk_type);
                    return (v.id == risk_type)
                })
            })
            // let risks = await Core.getHttp(`/api/risk/?year=${Core.currentYear.id}&risk_type=${risk_type}&agency=${this.user.agency_id}`)
            // return risks.length
            console.log(risks)
            return risks.length
        }

    },
}
</script>

